SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_apropriacao_pck.desfazer_aprop_fec_lote_pgto ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_vl_item_w		pls_util_cta_pck.t_number_table;
					
c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_seq_pp_it_aprop
	from	pls_pp_item_lote a,
		pls_pp_item_apropriacao b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	a.ie_acao_negativo = 'N'
	and	b.nr_sequencia = a.nr_seq_pp_it_aprop
	and	b.ie_baixado = 'S';

c02 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.vl_item,
		a.nr_seq_pp_it_aprop
	from	pls_pp_item_lote a,
		pls_pp_item_apropriacao b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	a.ie_acao_negativo = 'S'
	and	b.nr_sequencia = a.nr_seq_pp_it_aprop;

c03 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia
	from	pls_pp_item_lote a,
		pls_pp_item_apropriacao b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	b.nr_seq_it_lote_orig = a.nr_sequencia;


BEGIN

-- cursor que retorna os itens que foram setados para pago neste lote de pagamento
open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into	tb_nr_sequencia_w
	limit pls_util_cta_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
	
	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		update	pls_pp_item_apropriacao
		set	ie_baixado = 'N',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c01;

-- cursor que retorna todos os registros que entraram neste lote e n√£o foram apropriados totalmente
open c02(nr_seq_lote_p);
loop
	fetch c02 bulk collect into	tb_nr_sequencia_w, tb_vl_item_w
	limit pls_util_cta_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		update	pls_pp_item_apropriacao
		set	vl_divida = tb_vl_item_w(i),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c02;

-- retorna os itens que foram gerados neste lote de pagamento
open c03(nr_seq_lote_p);
loop
	fetch c03 bulk collect into	tb_nr_sequencia_w
	limit pls_util_cta_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
	
	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		delete
		from	pls_pp_item_apropriacao
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c03;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
