SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_apropriacao_pck.desfazer_aprop_ger_lote_pgto ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_pp_item_lote a,
		pls_pp_item_apropriacao b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	b.nr_sequencia = a.nr_seq_pp_it_aprop;


BEGIN
-- aqui limpa todos os registros da pls_pp_item_lote com origem de apropriação
open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into	tb_nr_sequencia_w
	limit pls_util_cta_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
	
	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		delete
		from	pls_pp_item_lote
		where	nr_seq_lote = nr_seq_lote_p
		and	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
