SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_pp_apropriacao_pck.obter_pgto_aprop_posteriores ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT 	to_char(a.nr_seq_lote) nr_seq_lote
	from	pls_pp_item_lote a,
		pls_pp_item_apropriacao b,
		pls_pp_item_lote c
	where	c.nr_seq_lote = nr_seq_lote_pc
	and	b.nr_seq_it_lote_orig = c.nr_sequencia
	and	a.nr_seq_pp_it_aprop = b.nr_sequencia;

c02 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	to_char(c.nr_seq_lote) nr_seq_lote
	from	pls_pp_item_lote a,
		pls_pp_item_apropriacao b,
		pls_pp_item_lote c
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	b.nr_sequencia = a.nr_seq_pp_it_aprop
	and	c.nr_seq_pp_it_aprop = b.nr_sequencia
	and	c.dt_atualizacao > a.dt_atualizacao
	and	c.nr_seq_lote != nr_seq_lote_pc;
BEGIN

-- verifica se existe algum registro de apropriacao gerado neste lote que tenha entrado em algum lote posterior
for r_c01_w in c01(nr_seq_lote_p) loop
	
	ds_retorno_w := pls_util_pck.concatena_string(	ds_retorno_w, r_c01_w.nr_seq_lote);
end loop;

-- se n√£o entrou nenhum registro gerado neste lote de pagamento, precisa ainda ver se entrou algum gerado em outro lote
if (coalesce(ds_retorno_w::text, '') = '') then

	for r_c02_w in c02(nr_seq_lote_p) loop
	
		ds_retorno_w := pls_util_pck.concatena_string(	ds_retorno_w, r_c02_w.nr_seq_lote);
	end loop;
end if;

return substr(ds_retorno_w, 1, 255);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
