SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_canc_pgto_prest_pck.cancelar_ie_tipo_item_3 ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nr_seq_pp_prest_p pls_pp_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_devolucao_w	adiant_pago_dev.nr_sequencia%type;
ds_observacao_w		adiant_pago_dev.ds_motivo_dev%type;

c01 CURSOR(	nr_seq_lote_pc		pls_pp_lote.nr_sequencia%type,
		nr_seq_pp_prest_pc	pls_pp_prestador.nr_sequencia%type) FOR
	SELECT	c.nr_adiantamento,
		c.vl_item,
		d.cd_moeda
	from	pls_pp_prest_event_prest a,
		pls_pp_it_prest_event_val b,
		pls_pp_item_lote c,
		adiantamento_pago d
	where	a.nr_seq_pp_prest = nr_seq_pp_prest_pc
	and	b.nr_seq_prest_even_val = a.nr_seq_pp_prest_even_val
	and	c.nr_seq_lote = nr_seq_lote_pc
	and	c.nr_sequencia = b.nr_seq_item_lote
	and	c.ie_tipo_item = '3'
	and	d.nr_adiantamento = c.nr_adiantamento;

BEGIN
-- retorna todos os registros de adiantamento

for r_c01_w in c01(nr_seq_lote_p, nr_seq_pp_prest_p) loop

	-- busca a sequencia da devolucao

	select	coalesce(max(a.nr_sequencia), 0) + 1
	into STRICT	nr_seq_devolucao_w
	from	adiant_pago_dev a;

	ds_observacao_w := substr('Devolucao gerada atraves do cancelamento de pagamento no lote de pagamento ' || nr_seq_lote_p || pls_util_pck.enter_w ||
				'. Funcao OPS - Pagamentos de Producao Medica (nova).', 1, 255);

	-- insere o registro de devolucao com uma observacao informando a origem

	insert into adiant_pago_dev(
		nr_adiantamento, nr_sequencia, dt_devolucao,
		vl_devolucao, cd_moeda, dt_atualizacao,
		nm_usuario, ds_motivo_dev
	) values (
		r_c01_w.nr_adiantamento, nr_seq_devolucao_w, clock_timestamp(),
		r_c01_w.vl_item, r_c01_w.cd_moeda, clock_timestamp(),
		nm_usuario_p, ds_observacao_w
	);

	CALL atualizar_saldo_adiant_pago(r_c01_w.nr_adiantamento, nm_usuario_p);

	commit;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
