CREATE OR REPLACE PROCEDURE pls_pp_canc_pgto_prest_pck.cancelar_titulo_pgto_prest ( nr_seq_pp_prest_p pls_pp_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_titulo_pagar_w	titulo_pagar.nr_titulo%type;
nr_titulo_receber_w	titulo_receber.nr_titulo%type;

c01 CURSOR(	nr_seq_pp_prest_pc	pls_pp_prestador.nr_sequencia%type) FOR
	SELECT	e.nr_titulo
	from	pls_pp_prest_event_prest a,
		pls_pp_it_prest_event_val b,
		pls_pp_base_atual_trib c,
		pls_pp_valor_trib_pessoa d,
		titulo_pagar e
	where	a.nr_seq_pp_prest = nr_seq_pp_prest_pc
	and	b.nr_seq_prest_even_val = a.nr_seq_pp_prest_even_val
	and	c.nr_seq_item_lote = b.nr_seq_item_lote
	and	d.nr_sequencia = c.nr_seq_trib_pessoa
	and	e.nr_titulo = d.nr_titulo_pagar;

BEGIN

select	nr_titulo_pagar,
	nr_titulo_receber
into STRICT	nr_titulo_pagar_w,
	nr_titulo_receber_w
from	pls_pp_prestador
where	nr_sequencia = nr_seq_pp_prest_p;

-- verifica se tem titulo a pagar

if (nr_titulo_pagar_w IS NOT NULL AND nr_titulo_pagar_w::text <> '') then

	CALL cancelar_titulo_pagar(nr_titulo_pagar_w, nm_usuario_p, clock_timestamp());
end if;

-- verifica se tem titulo a receber

if (nr_titulo_receber_w IS NOT NULL AND nr_titulo_receber_w::text <> '') then

	CALL cancelar_titulo_receber(nr_titulo_receber_w, nm_usuario_p, 'N', clock_timestamp());
end if;

-- cancela todos os titulos de tributos gerados

for r_c01_w in c01(nr_seq_pp_prest_p) loop

	CALL cancelar_titulo_pagar(r_c01_w.nr_titulo, nm_usuario_p, clock_timestamp());
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
