CREATE OR REPLACE PROCEDURE pls_pp_filtro_ocor_fin_pck.gerencia_selecao_filtro ( nr_id_transacao_p pls_pp_rp_ofin_selecao.nr_id_transacao%type, nr_seq_filtro_p pls_pp_rp_cta_filtro.nr_sequencia%type, ie_excecao_p pls_pp_rp_cta_filtro.ie_excecao%type, tb_seq_prestador_p pls_util_cta_pck.t_number_table, tb_seq_evento_p pls_util_cta_pck.t_number_table, tb_seq_selecao_p pls_util_cta_pck.t_number_table, ie_valido_p pls_pp_rp_ofin_selecao.ie_valido%type) AS $body$
DECLARE

					
tb_atualizar_seqs_w	pls_util_cta_pck.t_number_table;
tb_gravar_prestador_w	pls_util_cta_pck.t_number_table;
tb_gravar_evento_w	pls_util_cta_pck.t_number_table;
nr_idx_atual_w		integer;
nr_idx_gravar_w		integer;

BEGIN

-- deve ter informação da transação para executar alguma alteração.
if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') and (tb_seq_prestador_p.count > 0) then

	-- quando não for uma regra de exceção então deve ser verificado os filtros para decidir se irá inserir ou atualizar.
	if (ie_excecao_p = 'N') then
	
		-- limpa as listas
		tb_atualizar_seqs_w.delete;
		tb_gravar_prestador_w.delete;
		tb_gravar_evento_w.delete;
		
		-- zerar os indices das listas de inserção e atualização.
		nr_idx_atual_w := 0;
		nr_idx_gravar_w := 0;
		
		-- verifica se tem registros no vetor de seleção
		if (tb_seq_selecao_p.count > 0) then
		
			-- varrer os registros das listas passadas para verficar se irá inserir ou atualizar os registros.
			for i in tb_seq_selecao_p.first .. tb_seq_selecao_p.last loop
			
				-- se tiver informação das sequencias do item ou dos filhos então deve atualizar.
				if ((tb_seq_selecao_p(i) IS NOT NULL AND (tb_seq_selecao_p(i))::text <> '')) then
				
					-- joga para as variáveis que serão utilizadas para a atualizacao
					tb_atualizar_seqs_w(nr_idx_atual_w) := tb_seq_selecao_p(i);
					
					-- incrementar o indice.
					nr_idx_atual_w := nr_idx_atual_w + 1;
					
				-- se não é um novo registro e este deve ser inserido.
				else
					-- inserir um novo registro.
					tb_gravar_prestador_w(nr_idx_gravar_w) := tb_seq_prestador_p(i);
					tb_gravar_evento_w(nr_idx_gravar_w) := tb_seq_evento_p(i);
					
					-- incrementar o indice.
					nr_idx_gravar_w := nr_idx_gravar_w + 1;
				end if;
			end loop;
		end if;
		
		-- atualiza os registros passados por parâmetro no banco.
		CALL CALL CALL CALL CALL pls_pp_filtro_ocor_fin_pck.atualizar_lista_filtro(tb_atualizar_seqs_w, ie_valido_p);
		
		-- se tiver algum registro para gravar então manda ele pro banco.
		CALL CALL CALL CALL CALL pls_pp_filtro_ocor_fin_pck.gravar_lista_filtro(	nr_id_transacao_p, nr_seq_filtro_p,
					tb_gravar_prestador_w, tb_gravar_evento_w,
					ie_valido_p);
					
	-- caso for uma regra de exceção então atualiza o registro da seleção apenas, EXCEÇÃO NÃO INSERE NINGUÉM.
	elsif (ie_excecao_p = 'S') then
		CALL CALL CALL CALL CALL pls_pp_filtro_ocor_fin_pck.atualizar_lista_filtro(tb_seq_selecao_p, ie_valido_p);
	end if;
end if;

END;

$body$
