CREATE OR REPLACE PROCEDURE pls_pp_filtro_prod_medica_pck.gerencia_selecao_prod_medica ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

					
nr_id_transacao_w	pls_pp_rp_cta_selecao.nr_id_transacao%type;

BEGIN

-- verifica se foi passado algum parametro, caso nao tenha sido passado nao faz nada esta validacao e para evitar que seja processado toda a tabela por nao ter uma restricao
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	
	-- carrega os parametros necessarios para processar os dados
	CALL pls_pp_lote_pagamento_pck.carrega_parametros(nr_seq_lote_p, cd_estabelecimento_p);
	
	for r_c_dados_lote_w in pls_pp_lote_pagamento_pck.c_dados_lote(nr_seq_lote_p) loop
		
		-- gera a transacao para o processamento do lote
		select 	nextval('pls_id_transacao_pp_rp_cta_seq')
		into STRICT	nr_id_transacao_w
		;
	
		-- processa os filtros da regra e deixa os registros validos na tabela pls_pp_rp_cta_selecao
		CALL CALL CALL CALL CALL pls_pp_filtro_prod_medica_pck.gerencia_aplicacao_filtro(	r_c_dados_lote_w.nr_seq_lote, r_c_dados_lote_w.nr_seq_regra_periodo,
						nr_id_transacao_w, r_c_dados_lote_w.ie_tipo_dt_referencia,
						r_c_dados_lote_w.dt_referencia_inicio, r_c_dados_lote_w.dt_referencia_fim,
						r_c_dados_lote_w.ie_recurso_proprio, r_c_dados_lote_w.cd_estabelecimento,
						nm_usuario_p);

		-- grava na tabela pls_pp_item_lote os itens de producao medica que pertencem ao lote
		CALL CALL pls_pp_filtro_prod_medica_pck.vincula_item_lote_producao(	nr_id_transacao_w, nr_seq_lote_p, r_c_dados_lote_w.nr_seq_regra_periodo,
						nm_usuario_p);
		
		-- limpa a tabela de selecao
		CALL CALL CALL CALL CALL pls_pp_filtro_prod_medica_pck.limpar_transacao(nr_id_transacao_w);
	end loop;
end if;

END;

