CREATE OR REPLACE FUNCTION pls_pp_filtro_prod_medica_pck.obter_restricao_contrato ( nr_seq_contrato_p pls_pp_rp_filtro_contr.nr_seq_contrato%type, nr_seq_grupo_contrato_p pls_pp_rp_filtro_contr.nr_seq_grupo_contrato%type, ie_tipo_data_ref_p pls_pp_lote.ie_tipo_dt_referencia%type, ds_campos_p out text, ds_tabela_p out text, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
