CREATE OR REPLACE PROCEDURE pls_pp_filtro_rec_glosa_pck.atualiza_campo_auxiliar ( nr_id_transacao_p pls_pp_cta_rec_selecao.nr_id_transacao%type, nr_seq_filtro_p pls_pp_rp_cta_filtro.nr_sequencia%type, ie_excecao_p pls_pp_rp_cta_filtro.ie_excecao%type) AS $body$
BEGIN

-- se for excecao

if (ie_excecao_p = 'S') then
	update	pls_pp_cta_rec_selecao
	set	ie_valido_temp = 'N'
	where 	nr_id_transacao = nr_id_transacao_p
	and	ie_excecao = 'S';
else	-- qualquer outro caso sempre leva em consideracao os filtros
	update	pls_pp_cta_rec_selecao
	set	ie_valido_temp = 'N'
	where 	nr_id_transacao = nr_id_transacao_p
	and	nr_seq_filtro = nr_seq_filtro_p
	and 	ie_valido = 'S';
end if;

commit;

END;

