CREATE OR REPLACE PROCEDURE pls_pp_gerar_titulo_pck.cancelar_desvinc_titulo_rec ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, dt_cancelamento_p timestamp, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		a.nr_titulo_receber
	from	pls_pp_prestador a,
		titulo_receber b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	a.vl_titulo_receber > 0
	and	b.nr_titulo = a.nr_titulo_receber;

BEGIN
-- retorna todos os titulos a receber que foram gerados para os prestadores do lote

for r_c01_w in c01(nr_seq_lote_p) loop

	-- cancela os titulos na data passada de parametro

	CALL cancelar_titulo_receber(r_c01_w.nr_titulo_receber, nm_usuario_p, 'N', dt_cancelamento_p);

	update	pls_pp_prestador
	set	nr_titulo_receber  = NULL
	where	nr_sequencia = r_c01_w.nr_sequencia;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
