CREATE OR REPLACE PROCEDURE pls_pp_gerar_titulo_pck.cancelar_tit_pagar_terceiro ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, dt_cancelamento_p timestamp, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		a.nr_tit_pagar_terceiro
	from	pls_pp_item_lote a,
		titulo_pagar b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	a.ie_tipo_item = '2'
	and	b.nr_titulo = a.nr_tit_pagar_terceiro;

BEGIN
-- retorna todos os titulos a pagar gerados no lote de pagamento

for r_c01_w in  c01(nr_seq_lote_p) loop

	-- cancela os titulos na data passada de parametro

	CALL cancelar_titulo_pagar(r_c01_w.nr_tit_pagar_terceiro, nm_usuario_p, dt_cancelamento_p);

	update	pls_pp_item_lote
	set	nr_tit_pagar_terceiro  = NULL
	where	nr_sequencia = r_c01_w.nr_sequencia;
end loop;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
