CREATE OR REPLACE PROCEDURE pls_pp_gerar_titulo_pck.cancelar_titulo_tributo_lote ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, dt_cancelamento_p timestamp, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		a.nr_titulo_pagar
	from	pls_pp_valor_trib_pessoa a,
		titulo_pagar b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	b.nr_titulo = a.nr_titulo_pagar;

BEGIN
-- retorna todos os impostos que tiveram titulos gerados para cancelar os mesmos

for r_c01_w in c01(nr_seq_lote_p) loop

	CALL cancelar_titulo_pagar(r_c01_w.nr_titulo_pagar, nm_usuario_p, dt_cancelamento_p);

	update	pls_pp_valor_trib_pessoa
	set	nr_titulo_pagar  = NULL
	where	nr_sequencia = r_c01_w.nr_sequencia;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
