CREATE OR REPLACE FUNCTION pls_pp_gerar_titulo_pck.obter_pgto_escritural_prest ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nr_seq_prest_pag_p pls_pp_prestador.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


nr_seq_escritura_w	banco_escritural.nr_sequencia%type;


BEGIN
-- precisa ser passado um prestador do pagamento, pois pode existir mais de um pagamento escritural para o mesmo lote

if (nr_seq_prest_pag_p IS NOT NULL AND nr_seq_prest_pag_p::text <> '') then

	select	max(a.nr_seq_escrit)
	into STRICT	nr_seq_escritura_w
	from	titulo_pagar_escrit a,
		pls_pp_prestador b,
		banco_escritural c
	where	b.nr_seq_lote = nr_seq_lote_p
	and	b.nr_sequencia = nr_seq_prest_pag_p
	and	a.nr_titulo = b.nr_titulo_pagar
	and	c.nr_seq_pp_lote = nr_seq_lote_p
	and	a.nr_seq_escrit = c.nr_sequencia;
end if;

return nr_seq_escritura_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
