SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_pp_gerar_titulo_pck.obter_prest_sem_nf_pgto ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


qt_sem_fatura_w		integer	:= 0;
ds_mensagem_w		varchar(255);


BEGIN

if (current_setting('pls_pp_gerar_titulo_pck.ie_gerar_tit_sem_nf_w')::pls_parametro_pagamento.ie_gerar_tit_sem_nf%type = 'N') then
	select	count(1)
	into STRICT	qt_sem_fatura_w
	from	pls_pp_prestador	a
	where	ie_cancelado		= 'N'
	and not exists (	SELECT	1
				from	pls_pp_prest_nota	b
				where	b.nr_seq_lote		= a.nr_seq_lote
				and	b.nr_seq_prestador	= a.nr_seq_prestador)
	and	a.nr_seq_lote 	= nr_seq_lote_p;
	
	if (qt_sem_fatura_w > 0) then
		ds_mensagem_w	:= '233171';
	end if;
end if;

return ds_mensagem_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
