CREATE OR REPLACE FUNCTION pls_pp_gerar_titulo_pck.obter_titulo_pagar_bordero ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nr_seq_pp_prest_p pls_pp_prestador.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(200);

c01 CURSOR(	nr_seq_lote_pc		pls_pp_lote.nr_sequencia%type,
		nr_seq_pp_prest_pc	pls_pp_prestador.nr_sequencia%type) FOR
	SELECT	a.nr_titulo_pagar,
		b.nr_bordero
	from	pls_pp_prestador a,
		titulo_pagar_bordero_v b
	where	coalesce(nr_seq_pp_prest_pc::text, '') = ''
	and	a.nr_seq_lote = nr_seq_lote_pc
	and	b.nr_titulo = a.nr_titulo_pagar
	
union all

	SELECT	a.nr_titulo_pagar,
		b.nr_bordero
	from	pls_pp_prestador a,
		titulo_pagar_bordero_v b
	where	a.nr_sequencia = nr_seq_pp_prest_pc
	and	b.nr_titulo = a.nr_titulo_pagar;

BEGIN
-- retorna os titulos que por ventura ja estejam em um bordero

for r_c01_w in  c01(nr_seq_lote_p, nr_seq_pp_prest_p) loop
	ds_retorno_w := pls_util_pck.concatena_string(ds_retorno_w, wheb_mensagem_pck.get_texto(1110214, 'NR_TITULO_PAGAR=' || r_c01_w.nr_titulo_pagar || ';' || 'NR_BORDERO=' || r_c01_w.nr_bordero),
							'.' || pls_util_pck.enter_w, 200);
end loop;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
