CREATE OR REPLACE PROCEDURE pls_pp_gerar_titulo_pck.valida_se_pode_gerar_escrit ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) AS $body$
DECLARE


qt_registro_w	integer;


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	banco_escritural
where	nr_seq_pp_lote = nr_seq_lote_p;

-- se ja existe um pagamento gerado para este lote entao barra

if (qt_registro_w > 0) then

	-- Ja existe um pagamento escritural para o lote selecionado

	CALL wheb_mensagem_pck.exibir_mensagem_abort(267209);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
