CREATE OR REPLACE PROCEDURE pls_pp_lanc_programado_pck.lanca_vl_fixo ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nr_seq_prest_origem_p pls_prestador.nr_sequencia%type, nr_seq_evento_p pls_evento.nr_sequencia%type, dt_item_p pls_pp_item_lote_lp_v.dt_item%type, nr_seq_lanc_prog_p pls_pp_lanc_programado.nr_sequencia%type, vl_item_p pls_pp_item_lote_lp_v.vl_item%type, nm_usuario_p usuario.nm_usuario%type, cd_centro_custo_p pls_pp_item_lote.cd_centro_custo%type) AS $body$
DECLARE


qt_registro_w		integer;
nr_seq_novo_item_w	pls_pp_item_lote_lp_v.nr_sequencia%type;


BEGIN
-- inicialização
nr_seq_novo_item_w := null;

select	count(1)
into STRICT	qt_registro_w
from	pls_pp_lote_prest_event b
where	b.nr_seq_lote = nr_seq_lote_p
and	b.nr_seq_prestador = nr_seq_prestador_p
