	nr_seq_novo_item_w := pls_pp_lote_pagamento_pck.gera_pp_item_lote(	nr_seq_lote_p, '2', nr_seq_prestador_p, nr_seq_prest_origem_p, nr_seq_evento_p, dt_item_p, nm_usuario_p, cd_centro_custo_p, nr_seq_novo_item_w, nr_seq_lanc_prog_p, null, vl_item_p, 0, vl_item_p);
