CREATE OR REPLACE FUNCTION pls_pp_lanc_programado_pck.obter_ordem_sugerida_regra ( nr_seq_pp_lanc_programado_p pls_pp_lanc_programado.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_valor_total_w	pls_pp_lanc_programado.nr_ordem_execucao%type;

C01 CURSOR(	nr_seq_pp_lanc_programado_pc 	pls_pp_lanc_programado.nr_sequencia%type) FOR
	SELECT	dt_inicio_vigencia,
		dt_fim_vigencia,
		nr_seq_prestador,
		cd_prestador_cod,
		nr_seq_tipo_prestador,
		ie_cooperado,
		nr_seq_sit_coop,
		ie_prestador_matriz
	from	pls_pp_lanc_programado
	where	nr_sequencia = nr_seq_pp_lanc_programado_pc;

BEGIN
-- iniciliza a vari√°vel
nr_valor_total_w := 0;

-- verifica a regra e adiciona 500 pontos para cada campo preenchido
for r_C01_w in C01( nr_seq_pp_lanc_programado_p ) loop
	-- campo dt_inicio_vigencia
	if (r_C01_w.dt_inicio_vigencia IS NOT NULL AND r_C01_w.dt_inicio_vigencia::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;

	-- campo dt_fim_vigencia
	if (r_C01_w.dt_fim_vigencia IS NOT NULL AND r_C01_w.dt_fim_vigencia::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;

	-- campo nr_seq_prestador
	if (r_C01_w.nr_seq_prestador IS NOT NULL AND r_C01_w.nr_seq_prestador::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;

	-- campo cd_prestador_cod
	if (r_C01_w.cd_prestador_cod IS NOT NULL AND r_C01_w.cd_prestador_cod::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;

	-- campo nr_seq_tipo_prestador
	if (r_C01_w.nr_seq_tipo_prestador IS NOT NULL AND r_C01_w.nr_seq_tipo_prestador::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;

	-- campo ie_cooperado
	if (r_C01_w.ie_cooperado = 'S') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;

	-- campo nr_seq_sit_coop
	if (r_C01_w.nr_seq_sit_coop IS NOT NULL AND r_C01_w.nr_seq_sit_coop::text <> '') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;

	-- campo ie_prestador_matriz
	if (r_C01_w.ie_prestador_matriz = 'S') then
		nr_valor_total_w := nr_valor_total_w + 500;
	end if;
end loop;

return nr_valor_total_w;

END;

$body$
