CREATE OR REPLACE PROCEDURE pls_pp_lanc_programado_pck.remover_lancamentos_zerados (nr_seq_lote_p pls_pp_lote.nr_sequencia%type) AS $body$
DECLARE


tb_rowid_w	pls_util_cta_pck.t_rowid;

C01 CURSOR FOR
	SELECT	oid
	from	pls_pp_item_lote_origem	a
	where	a.nr_seq_item in (	SELECT	b.nr_sequencia
					from	pls_pp_item_lote	b
					where	b.nr_seq_lote 		= nr_seq_lote_p
					and	b.ie_tipo_item 		= 2
					and	b.vl_item 		= 0);

C02 CURSOR FOR
	SELECT	oid
	from	pls_pp_item_lote_origem	a
	where	a.nr_seq_item_origem in (	SELECT	b.nr_sequencia
						from	pls_pp_item_lote  b
						where	b.nr_seq_lote	= nr_seq_lote_p
						and	b.ie_tipo_item	= 2
						and	vl_item		= 0);


BEGIN
open C01;
loop
	fetch C01 bulk collect into tb_rowid_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_rowid_w.count = 0;

	-- Remover os lançamentos que tiveram valor zerado
	forall	i in tb_rowid_w.first..tb_rowid_w.last
		delete	FROM pls_pp_item_lote_origem
		where	rowid	= tb_rowid_w(i);
	commit;

	tb_rowid_w.delete;
end loop;

if (C01%isOpen) then
	close C01;
end if;

open C02;
loop
	fetch C02 bulk collect into tb_rowid_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_rowid_w.count = 0;

	-- Remover os lançamentos que tiveram valor zerado
	forall	i in tb_rowid_w.first..tb_rowid_w.last
		delete	FROM pls_pp_item_lote_origem
		where	rowid	= tb_rowid_w(i);
	commit;

	tb_rowid_w.delete;
end loop;

if (C02%isOpen) then
	close C02;
end if;

-- Remover os lançamentos que tiveram valor zerado
delete	FROM pls_pp_item_lote
where	nr_seq_lote	= nr_seq_lote_p
and	ie_tipo_item	= 2
and	vl_item		= 0;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
