CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.alimentar_registros_nulos ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_ie_acao_neg_w	pls_util_cta_pck.t_varchar2_table_1;

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	nr_sequencia,
		coalesce(ie_acao_negativo, 'N') ie_acao_negativo
	from	pls_pp_item_lote
	where	nr_seq_lote = nr_seq_lote_pc;


BEGIN

open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into	tb_nr_sequencia_w, tb_ie_acao_neg_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		update	pls_pp_item_lote
		set	ie_acao_negativo = tb_ie_acao_neg_w(i)
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
