CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.atualiza_dados_ger_lote_pgto ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, ie_sucesso_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
-- se o processo for de geracao do lote e for um sucesso

if (ie_sucesso_p = 'S') then

	update	pls_pp_lote
	set	dt_geracao_lote = clock_timestamp(),
		ie_status = 'P',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;

	-- atualiza o campo para garantir que nao sera possivel realizar alteracao no cadastro do periodo / deixar o cliente dar manutencao nos cadastros

	/*for r_c01_w in pls_pp_lote_pagamento_pck.c_dados_lote(nr_seq_lote_p) loop
		
		update	pls_pp_regra_periodo
		set	ie_utilizado = 'S',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = sysdate
		where	nr_sequencia = r_c01_w.nr_seq_regra_periodo;
	end loop;*/


-- caso der algum erro	

elsif (ie_sucesso_p = 'N') then

	update	pls_pp_lote
	set	dt_geracao_lote  = NULL,
		ie_status = 'E',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
