CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.atualiza_dados_ger_tit_lote ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, ie_sucesso_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

-- se o processo for de geracao do lote e for um sucesso

if (ie_sucesso_p = 'S') then

	update	pls_pp_lote
	set	dt_geracao_titulo = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ie_status = 'D'
	where	nr_sequencia = nr_seq_lote_p;

-- caso der algum erro	

elsif (ie_sucesso_p = 'N') then

	update	pls_pp_lote
	set	dt_geracao_titulo  = NULL,
		ie_status = 'H',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
