-- procedure utilizada para sinalizar que esta sendo feito algo com o lote



CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.atualiza_dados_trabalho_lote ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

					
dados_sid_serial_w	pls_util_pck.dados_sid;

BEGIN

-- busca os dados da sessao atual

dados_sid_serial_w := pls_util_pck.obter_sid_sessao_atual;

-- coloca com status em processamento.

update	pls_pp_lote
set	ie_status = 'G',
	nm_id_sid = dados_sid_serial_w.nm_id_sid,
	nm_id_serial = dados_sid_serial_w.nm_id_serial,
	nm_usuario = nm_usuario_p, 
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_lote_p;
commit;

END;

$body$
