CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.atualiza_valor_negativo ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, tb_nr_sequencia_p INOUT pls_util_cta_pck.t_number_table, tb_vl_item_p INOUT pls_util_cta_pck.t_number_table, nr_cont_p INOUT integer) AS $body$
BEGIN
-- se tiver algo entao faz o update

if (tb_nr_sequencia_p.count > 0) then

	forall i in tb_nr_sequencia_p.first..tb_nr_sequencia_p.last
		update	pls_pp_item_lote
		set	vl_acao_negativo = tb_vl_item_p(i),
			ie_acao_negativo = 'S'
		where	nr_sequencia = tb_nr_sequencia_p(i);
	commit;
end if;

tb_nr_sequencia_p.delete;
tb_vl_item_p.delete;
nr_cont_p := 0;

END;

$body$
