SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.desfazer_desc_disc ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) AS $body$
DECLARE

-- Busca todos os itens de desconto de discussao

tb_sequencia_w		pls_util_cta_pck.t_number_table;

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT 	nr_sequencia
	from	pls_pp_item_lote
	where	nr_seq_lote = nr_seq_lote_pc
	and	ie_tipo_item = '10';

BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	
	-- Carrega os itens do tipo 10 e exclui eles

	open c01(nr_seq_lote_p);
	loop
	fetch c01 bulk collect into tb_sequencia_w limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_sequencia_w.count = 0;
	
		forall i in tb_sequencia_w.first..tb_sequencia_w.last
			delete	FROM pls_pp_item_lote
			where	nr_sequencia	= tb_sequencia_w(i);
			
		commit;
		tb_sequencia_w.delete;
	end loop;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
