CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.desfazer_item_prod_prest ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type) AS $body$
DECLARE


tb_sequencia_w		pls_util_cta_pck.t_number_table;

C01 CURSOR( nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia
	from	pls_pp_prest_evento_valor a,
		pls_pp_prest_event_prest b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	b.nr_seq_pp_prest_even_val = a.nr_sequencia;
	
C02 CURSOR( nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_pp_prestador
	where	nr_seq_lote = nr_seq_lote_pc
	order by coalesce(nr_seq_origem_estorno, 0) desc;
	

BEGIN

-- percorre o cursor e apaga os itens retornados

open C01( nr_seq_lote_p );
	loop
	fetch c01 bulk collect into tb_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_sequencia_w.count = 0;
	
	forall i in tb_sequencia_w.first..tb_sequencia_w.last
		delete 	from pls_pp_prest_event_prest
		where	nr_sequencia = tb_sequencia_w(i);
	commit;
	end loop;
close C01;

-- percorre o cursor e apaga os historicos do pls_pp_prestador em questao

open C02( nr_seq_lote_p );
	loop
	fetch C02 bulk collect into tb_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_sequencia_w.count = 0;
	
	forall i in tb_sequencia_w.first..tb_sequencia_w.last
		delete 	from pls_pp_lote_pag_hist
		where	nr_seq_pp_prestador = tb_sequencia_w(i);
	commit;
	end loop;
close C02;

-- percorre o cursor e apaga os itens retornados

open C02( nr_seq_lote_p );
	loop
	fetch C02 bulk collect into tb_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_sequencia_w.count = 0;
	
	forall i in tb_sequencia_w.first..tb_sequencia_w.last
		delete 	from pls_pp_prestador
		where	nr_sequencia = tb_sequencia_w(i);
	commit;
	end loop;
close C02;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
