SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.estornar_vl_negativo_lote ( nr_seq_lote_p pls_pp_item_lote.nr_seq_lote%type) AS $body$
DECLARE


tb_nr_seq_pp_it_aprop_w		pls_util_cta_pck.t_number_table;
tb_vl_divida_w			pls_util_cta_pck.t_number_table;

c00 CURSOR(	nr_seq_lote_pc	pls_pp_item_lote.nr_seq_lote%type) FOR
	SELECT	a.nr_seq_pp_it_aprop,
		a.vl_item vl_divida
	from	pls_pp_item_lote a
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	(a.nr_seq_pp_it_aprop IS NOT NULL AND a.nr_seq_pp_it_aprop::text <> '')
	and	coalesce(a.vl_acao_negativo, 0) <> 0;
	

BEGIN

open c00(nr_seq_lote_p);
loop
	fetch c00 bulk collect into tb_nr_seq_pp_it_aprop_w, tb_vl_divida_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_pp_it_aprop_w.count = 0;

	forall i in tb_nr_seq_pp_it_aprop_w.first..tb_nr_seq_pp_it_aprop_w.last
		update	pls_pp_item_apropriacao
		set	vl_divida = tb_vl_divida_w(i)
		where	nr_sequencia = tb_nr_seq_pp_it_aprop_w(i);
end loop;
close c00;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
