CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.obter_dados_forma_pgto_prest ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, ie_acao_pgto_negativo_p INOUT pls_prestador_pagto.ie_saldo_negativo%type, qt_pag_negativo_max_p INOUT pls_prestador_pagto.qt_pag_negativo_max%type, vl_minimo_tit_liq_p INOUT pls_prestador_pagto.vl_minimo_tit_liq%type) AS $body$
DECLARE


ie_acao_pgto_negativo_w		pls_prestador_pagto.ie_saldo_negativo%type;
qt_pag_negativo_max_w		pls_prestador_pagto.qt_pag_negativo_max%type;
vl_minimo_tit_liq_w		pls_prestador_pagto.vl_minimo_tit_liq%type;


BEGIN

-- busca no cadastro do prestador pasta Dados complementares > Forma de pagamento os dados

-- os campos quantidade de dias e valor minino devem retornar null caso nao estiverem informados

-- isto por que sera feito um tratamento para caso os mesmos estiverem nulo entao iremos buscar as

-- informacoes do cadastro do evento

select	coalesce(max(a.ie_saldo_negativo), 'CP'),
	max(a.qt_pag_negativo_max),
	max(a.vl_minimo_tit_liq)
into STRICT	ie_acao_pgto_negativo_w,
	qt_pag_negativo_max_w,
	vl_minimo_tit_liq_w
from	pls_prestador_pagto a
where	a.nr_seq_prestador = nr_seq_prestador_p
and	current_setting('pls_pp_lote_pagamento_pck.dt_referencia_lote_ini_w')::pls_pp_lote.dt_referencia_inicio%type between a.dt_inicio_vigencia_ref and a.dt_fim_vigencia_ref;

-- se for conforme parametro entao retorna o parametro

if (ie_acao_pgto_negativo_w = 'CP') then

	ie_acao_pgto_negativo_w := current_setting('pls_pp_lote_pagamento_pck.ie_acao_pgto_neg_param_w')::pls_parametro_pagamento.ie_saldo_negativo%type;
end if;

-- alimenta os parametros out

ie_acao_pgto_negativo_p := ie_acao_pgto_negativo_w;
qt_pag_negativo_max_p := qt_pag_negativo_max_w;
vl_minimo_tit_liq_p := vl_minimo_tit_liq_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
