CREATE OR REPLACE FUNCTION pls_pp_lote_pagamento_pck.obter_obs_ev_fin ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nr_seq_evento_p pls_evento.nr_sequencia%type, nr_seq_item_p pls_pp_item_lote.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w		varchar(4000);
nr_seq_item_w		pls_pp_item_lote.nr_sequencia%type := nr_seq_item_p;
ie_tipo_item_w		pls_pp_item_lote.ie_tipo_item%type;
nr_seq_disc_evento_w	pls_pp_item_lote.nr_seq_disc_evento%type;
nr_seq_lanc_prog_w	pls_pp_item_lote.nr_seq_lanc_prog%type;


BEGIN
if (coalesce(nr_seq_item_w::text, '') = '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_item_w
	from	pls_pp_item_lote i
	where	i.nr_seq_lote		= nr_seq_lote_p
	and	i.nr_seq_prestador	= nr_seq_prestador_p
	and	i.nr_seq_evento		= nr_seq_evento_p
	and	i.ie_tipo_item		!= '6'; -- Nao pode ser apropriacao
end if;

-- Verificar se e apropriacao

select	coalesce(max(i.nr_sequencia),nr_seq_item_w)
into STRICT	nr_seq_item_w
from	pls_pp_item_apropriacao a,
	pls_pp_item_lote i,
	pls_pp_item_lote i2
where	i.nr_sequencia = a.nr_seq_it_lote_orig
and	a.nr_sequencia = i2.nr_seq_pp_it_aprop
and	i2.nr_sequencia= nr_seq_item_w;

if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') then
	select	max(ie_tipo_item),
		max(nr_seq_disc_evento),
		max(nr_seq_lanc_prog)
	into STRICT	ie_tipo_item_w,
		nr_seq_disc_evento_w,
		nr_seq_lanc_prog_w
	from	pls_pp_item_lote
	where	nr_sequencia	= nr_seq_item_w;
	
	-- PLS_PP_ITEM_LOTE.IE_TIPO_ITEM

	-- 1. Producao medica		2.Lancamentos programados	3. Adiantamento Pago

	-- 4. Titulo a receber		5. Titulos a Pagar		6. Apropriacao pagamento

	-- 7. Acerto de tributacao	8. Recurso de glosa		9. Franquia			

	-- 10. Desconto contestacao discussao

	
	if (ie_tipo_item_w = '2') and (nr_seq_lanc_prog_w IS NOT NULL AND nr_seq_lanc_prog_w::text <> '') then
		select	max(ds_observacao)
		into STRICT	ds_retorno_w
		from	pls_pp_lanc_programado
		where	nr_sequencia	= nr_seq_lanc_prog_w;
	
	elsif (ie_tipo_item_w = '10') and (nr_seq_disc_evento_w IS NOT NULL AND nr_seq_disc_evento_w::text <> '') then
		select	max(ds_observacao)
		into STRICT	ds_retorno_w
		from	pls_discussao_evento_movto
		where	nr_sequencia	= nr_seq_disc_evento_w;
	end if;
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
