CREATE OR REPLACE FUNCTION pls_pp_lote_pagamento_pck.obter_prestador_pagamento ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, dt_vencimento_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_prestador_w	pls_prestador.nr_sequencia%type;

c01 CURSOR(	nr_seq_prestador_pc	pls_prestador.nr_sequencia%type,
		dt_vencimento_pc	timestamp) FOR
	SELECT	nr_seq_prest_receb
	from	pls_prestador_crit_receb
	where	nr_seq_prestador = nr_seq_prestador_pc
	and	dt_vencimento_pc between dt_inicio_vigencia_ref and dt_fim_vigencia_ref
	order by dt_inicio_vigencia_ref desc, nr_sequencia desc;

BEGIN
-- inicia com o prestador passado de parametro, caso o cursor nao retorne registro e este prestador que deve ser retornado

nr_seq_prestador_w := nr_seq_prestador_p;

-- o cursor esta escrito de forma a retorna no primeiro registro o correto, desta forma assim que retornar um registro sai do cursor

for r_c01_w in c01(nr_seq_prestador_p, dt_vencimento_p) loop

	nr_seq_prestador_w := r_c01_w.nr_seq_prest_receb;
	exit;
end loop;

return nr_seq_prestador_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
