CREATE OR REPLACE PROCEDURE pls_pp_lote_pagamento_pck.valida_lote_retencao_ja_gerado ( dt_mes_comp_lote_p pls_pp_lr_lote.dt_mes_competencia%type, nr_seq_termo_msg_p bigint) AS $body$
DECLARE

qt_registro_w	integer;

BEGIN
select	count(1)
into STRICT	qt_registro_w
from	pls_pp_lr_lote
where	dt_mes_competencia = dt_mes_comp_lote_p
and	(dt_geracao IS NOT NULL AND dt_geracao::text <> '');

-- se ja foi gerado algum 

if (qt_registro_w > 0) then
	-- apresenta a mensagem de erro

	CALL wheb_mensagem_pck.exibir_mensagem_abort(nr_seq_termo_msg_p);
end if;

END;

