CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.atualiza_dados_ger_lote_ret ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type, ie_sucesso_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
-- se o processo for de geração do lote e for um sucesso
if (ie_sucesso_p = 'S') then

	update	pls_pp_lr_lote
	set	dt_geracao = clock_timestamp(),
		ie_status = 'P',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;

-- caso der algum erro	
elsif (ie_sucesso_p = 'N') then

	update	pls_pp_lr_lote
	set	dt_geracao  = NULL,
		ie_status = 'E',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
