CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.atualiza_desf_ger_lote_ret ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type, ie_sucesso_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

-- se der tudo certo
if (ie_sucesso_p = 'S') then

	update	pls_pp_lr_lote
	set	dt_geracao  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ie_status = 'P'
	where	nr_sequencia = nr_seq_lote_p;
	
else
	-- caso der algum erro
	update	pls_pp_lr_lote
	set	nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ie_status = 'E'
	where	nr_sequencia = nr_seq_lote_p;

end if;

commit;

END;

$body$
