CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.cancelar_tit_trib_retencao ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


c01 CURSOR(	nr_seq_lote_pc	pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	nr_titulo_pagar,
		nr_sequencia
	from	pls_pp_lr_trib_pessoa a
	where	nr_seq_lote_ret = nr_seq_lote_pc
	and	(nr_titulo_pagar IS NOT NULL AND nr_titulo_pagar::text <> '');

BEGIN

CALL pls_pp_lote_retencao_pck.valida_se_canc_tit_lote_fecha(nr_seq_lote_p);

-- retorna todos os impostos que tiveram t√≠tulos gerados para cancelar os mesmos
for r_c01_w in c01(nr_seq_lote_p) loop

	CALL cancelar_titulo_pagar(	r_c01_w.nr_titulo_pagar, nm_usuario_p, clock_timestamp());

	update	pls_pp_lr_trib_pessoa
	set	nr_titulo_pagar  = NULL
	where	nr_sequencia = r_c01_w.nr_sequencia;
end loop;

update	pls_pp_lr_lote
set	dt_geracao_titulo  = NULL
where	nr_sequencia = nr_seq_lote_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
