CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.desfazer_fechamento_lote ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_seq_trib_pag_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_lr_trib_w	pls_util_cta_pck.t_number_table;

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_lr_trib_pess,
		b.nr_sequencia nr_seq_vl_trib_pessoa
	from	pls_pp_lr_trib_pessoa a,
		pls_pp_valor_trib_pessoa b
	where	a.nr_seq_lote_ret = nr_seq_lote_pc
	and	b.nr_seq_lr_trib_pessoa = a.nr_sequencia;


BEGIN

open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into	tb_nr_seq_lr_trib_w, tb_nr_seq_trib_pag_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_lr_trib_w.count = 0;
	
	forall i in tb_nr_seq_trib_pag_w.first..tb_nr_seq_trib_pag_w.last
		update	pls_pp_valor_trib_pessoa
		set	nr_seq_lr_trib_pessoa  = NULL
		where	nr_sequencia = tb_nr_seq_trib_pag_w(i);
	commit;
	
	forall i in tb_nr_seq_lr_trib_w.first..tb_nr_seq_lr_trib_w.last
		update	pls_pp_lr_trib_pessoa
		set	vl_diferenca_base = 0,
			vl_diferenca_trib = 0
		where	nr_sequencia = tb_nr_seq_lr_trib_w(i);
	commit;
end loop;
close c01;

END;

$body$
