CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.desfazer_valores_retencao_pgto ( nr_seq_lote_pgto_p pls_pp_lr_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_seq_item_w	pls_util_cta_pck.t_number_table;

c01 CURSOR(	nr_seq_lote_pgto_pc	pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_pp_item_lote a
	where	a.nr_seq_lote = nr_seq_lote_pgto_pc
	and	a.ie_tipo_item = '7';


BEGIN

open c01(nr_seq_lote_pgto_p);
loop
	fetch c01 bulk collect into tb_nr_seq_item_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_item_w.count = 0;
	
	forall i in tb_nr_seq_item_w.first..tb_nr_seq_item_w.last
		delete
		from	pls_pp_item_lote
		where	nr_seq_lote = nr_seq_lote_pgto_p
		and	nr_sequencia = tb_nr_seq_item_w(i);
	commit;
end loop;
close c01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
