-- inicializa as variáveis
SELECT * FROM pls_pp_lote_retencao_pck.alimenta_pp_base_acum_nf_corpo(	nr_cont_w, tb_nr_sequencia_w, tb_vl_tributo_w, tb_vl_base_calc_w, tb_vl_item_w, tb_cd_pf_w, tb_nr_seq_prest_w, tb_nr_seq_tipo_prest_w, tb_nr_seq_nota_w, tb_vl_base_adic_w, tb_vl_trib_adic_w, tb_vl_base_nao_ret_w, tb_vl_nao_retido_w, nr_seq_lote_p, cd_tributo_p, nm_usuario_p) INTO STRICT _ora2pg_r;
 	nr_cont_w := _ora2pg_r.nr_cont_p; tb_nr_sequencia_w := _ora2pg_r.tb_nr_sequencia_p; tb_vl_tributo_w := _ora2pg_r.tb_vl_tributo_p; tb_vl_base_calc_w := _ora2pg_r.tb_vl_base_calc_p; tb_vl_item_w := _ora2pg_r.tb_vl_item_p; tb_cd_pf_w := _ora2pg_r.tb_cd_pf_p; tb_nr_seq_prest_w := _ora2pg_r.tb_nr_seq_prest_p; tb_nr_seq_tipo_prest_w := _ora2pg_r.tb_nr_seq_tipo_prest_p; tb_nr_seq_nota_w := _ora2pg_r.tb_nr_seq_nota_p; tb_vl_base_adic_w := _ora2pg_r.tb_vl_base_adic_p; tb_vl_trib_adic_w := _ora2pg_r.tb_vl_trib_adic_p; tb_vl_base_nao_ret_w := _ora2pg_r.tb_vl_base_nao_ret_p; tb_vl_nao_retido_w := _ora2pg_r.tb_vl_nao_retido_p;

-- abre o cursor com os registros que precisam ser verificados
for r_c01_w in c01(	cd_tributo_p, nr_seq_lote_p, current_setting('pls_pp_lote_retencao_pck.dt_mes_comp_lote_inicio_w')::timestamp,
			current_setting('pls_pp_lote_retencao_pck.dt_mes_comp_lote_fim_w')::timestamp) loop

	-- a função obter_se_base_calculo verifica se deve fazer parte da base de cálculo,
	-- nela é verificada se esse valor não é proveniente de outra origem, por exemplo:
	-- um valor de pagamento que está no título a pagar
	if (r_c01_w.ie_base_calculo = 'S') then
	
		-- começa como sendo válido
		ie_acumula_base_w := true;

		-- se for necessário restringir estabelecimento
		if (ie_rest_estab_p = 'S') then

			-- verifica se o estabelecimento é diferente
			if (r_c01_w.cd_estabelecimento != cd_estabelecimento_p) then

				ie_acumula_base_w := false;
			end if;
		end if;

		-- valida a origem do tributo
		if (r_c01_w.ie_origem_trib != 'N') then

			ie_acumula_base_w := false;
		end if;

		-- valida a origem do valor, se é retido em outra empresa
		if (r_c01_w.ie_pago_prev != 'V') then

			ie_acumula_base_w := false;
		end if;

		-- valida a situação
		if (r_c01_w.ie_situacao != '1') then

			ie_acumula_base_w := false;
		end if;
		
		-- se é para fazer parte da base de cálculo
		if (ie_acumula_base_w) then

			tb_nr_sequencia_w(nr_cont_w) := r_c01_w.nr_seq_interno;
			tb_vl_tributo_w(nr_cont_w) := r_c01_w.vl_tributo;
			tb_vl_base_calc_w(nr_cont_w) := r_c01_w.vl_base_calculo;
			tb_vl_item_w(nr_cont_w) := r_c01_w.vl_total_nota;
			tb_cd_pf_w(nr_cont_w) := r_c01_w.cd_pessoa_fisica;
			tb_nr_seq_prest_w(nr_cont_w) := r_c01_w.nr_seq_prestador;
			tb_nr_seq_tipo_prest_w(nr_cont_w) := r_c01_w.nr_seq_tipo_prestador;
			tb_nr_seq_nota_w(nr_cont_w) := r_c01_w.nr_seq_nf;
			tb_vl_base_adic_w(nr_cont_w) := r_c01_w.vl_base_adic;
			tb_vl_trib_adic_w(nr_cont_w) := r_c01_w.vl_trib_adic;
			tb_vl_base_nao_ret_w(nr_cont_w) := r_c01_w.vl_base_nao_retido;
			tb_vl_nao_retido_w(nr_cont_w) := r_c01_w.vl_nao_retido;

			-- se atingiu a quantidade manda pro banco
			if (nr_cont_w >= pls_util_pck.qt_registro_transacao_w) then

				SELECT * FROM pls_pp_lote_retencao_pck.alimenta_pp_base_acum_nf_corpo(	nr_cont_w, tb_nr_sequencia_w, tb_vl_tributo_w, tb_vl_base_calc_w, tb_vl_item_w, tb_cd_pf_w, tb_nr_seq_prest_w, tb_nr_seq_tipo_prest_w, tb_nr_seq_nota_w, tb_vl_base_adic_w, tb_vl_trib_adic_w, tb_vl_base_nao_ret_w, tb_vl_nao_retido_w, nr_seq_lote_p, cd_tributo_p, nm_usuario_p) INTO STRICT _ora2pg_r;
 	nr_cont_w := _ora2pg_r.nr_cont_p; tb_nr_sequencia_w := _ora2pg_r.tb_nr_sequencia_p; tb_vl_tributo_w := _ora2pg_r.tb_vl_tributo_p; tb_vl_base_calc_w := _ora2pg_r.tb_vl_base_calc_p; tb_vl_item_w := _ora2pg_r.tb_vl_item_p; tb_cd_pf_w := _ora2pg_r.tb_cd_pf_p; tb_nr_seq_prest_w := _ora2pg_r.tb_nr_seq_prest_p; tb_nr_seq_tipo_prest_w := _ora2pg_r.tb_nr_seq_tipo_prest_p; tb_nr_seq_nota_w := _ora2pg_r.tb_nr_seq_nota_p; tb_vl_base_adic_w := _ora2pg_r.tb_vl_base_adic_p; tb_vl_trib_adic_w := _ora2pg_r.tb_vl_trib_adic_p; tb_vl_base_nao_ret_w := _ora2pg_r.tb_vl_base_nao_ret_p; tb_vl_nao_retido_w := _ora2pg_r.tb_vl_nao_retido_p;
			else
				nr_cont_w := nr_cont_w + 1;
			end if;
		end if;
	end if;
end loop;

-- se sobrou algo manda pro banco
SELECT * FROM pls_pp_lote_retencao_pck.alimenta_pp_base_acum_nf_corpo(	nr_cont_w, tb_nr_sequencia_w, tb_vl_tributo_w, tb_vl_base_calc_w, tb_vl_item_w, tb_cd_pf_w, tb_nr_seq_prest_w, tb_nr_seq_tipo_prest_w, tb_nr_seq_nota_w, tb_vl_base_adic_w, tb_vl_trib_adic_w, tb_vl_base_nao_ret_w, tb_vl_nao_retido_w, nr_seq_lote_p, cd_tributo_p, nm_usuario_p) INTO STRICT _ora2pg_r;
 	nr_cont_w := _ora2pg_r.nr_cont_p; tb_nr_sequencia_w := _ora2pg_r.tb_nr_sequencia_p; tb_vl_tributo_w := _ora2pg_r.tb_vl_tributo_p; tb_vl_base_calc_w := _ora2pg_r.tb_vl_base_calc_p; tb_vl_item_w := _ora2pg_r.tb_vl_item_p; tb_cd_pf_w := _ora2pg_r.tb_cd_pf_p; tb_nr_seq_prest_w := _ora2pg_r.tb_nr_seq_prest_p; tb_nr_seq_tipo_prest_w := _ora2pg_r.tb_nr_seq_tipo_prest_p; tb_nr_seq_nota_w := _ora2pg_r.tb_nr_seq_nota_p; tb_vl_base_adic_w := _ora2pg_r.tb_vl_base_adic_p; tb_vl_trib_adic_w := _ora2pg_r.tb_vl_trib_adic_p; tb_vl_base_nao_ret_w := _ora2pg_r.tb_vl_base_nao_ret_p; tb_vl_nao_retido_w := _ora2pg_r.tb_vl_nao_retido_p;

