CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.gerar_base_acum_tit_pg_imp ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type, cd_tributo_p tributo.cd_tributo%type, ie_rest_estab_p tributo.ie_restringe_estab%type, ie_vencimento_p tributo.ie_vencimento%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


