		dt_comp_fim_pc		timestamp,
		ie_vencimento_pc	text,
		nr_seq_lote_pc		pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		b.cd_pessoa_fisica,
		c.nr_seq_tipo_prestador,
		max(a.vl_imposto) vl_imposto,
