	where	ie_vencimento_pc = 'V' -- por vencimento, irá utilizar a data de vencimento do prestador
	and	c.ie_tipo_prestador = 'PF'
	and	b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and	b.dt_vencimento_atual between dt_comp_inicio_pc and dt_comp_fim_pc
	and	b.ie_situacao in ('A','L')
	and	a.nr_titulo = b.nr_titulo
	and	a.cd_tributo = cd_tributo_pc
	and	a.ie_pago_prev = 'V'
	and	b.ie_origem_titulo != '25' -- Se for de origem pagamento médico (HTML), então não considera, já devia estar o tributo no próprio pagamento
	and	exists (	SELECT	1
			from	pls_pp_lr_base_trib x
			where	x.nr_seq_lote_ret = nr_seq_lote_pc
			and	x.nr_seq_prestador = c.nr_seq_prestador)
	group by b.cd_pessoa_fisica, a.nr_sequencia, c.nr_seq_tipo_prestador
	
union all

	select	a.nr_sequencia,
		b.cd_pessoa_fisica,
		c.nr_seq_tipo_prestador,
		max(a.vl_imposto) vl_imposto,
		max(a.vl_base_calculo) vl_base_calculo,
		max(b.vl_titulo) vl_titulo,
		max(b.cd_estabelecimento) cd_estabelecimento,
		obter_se_base_calculo(max(a.nr_titulo), 'TP') ie_base_calculo,
		max(c.nr_seq_prestador) nr_seq_prestador,
		max(a.nr_titulo) nr_titulo,
		max(a.vl_base_adic) vl_base_adic,
		max(a.vl_trib_adic) vl_trib_adic,
		max(a.vl_base_nao_retido) vl_base_nao_retido,
		max(a.vl_nao_retido) vl_nao_retido
	from	pls_pp_prestador_tmp c,
		titulo_pagar b,
		titulo_pagar_imposto a
	where	ie_vencimento_pc = 'C' -- por vencimento, irá utilizar a data de vencimento do prestador
	and	c.ie_tipo_prestador = 'PF'
	and	b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and	b.ie_situacao in ('A','L')
	and	a.nr_titulo = b.nr_titulo
	and	a.cd_tributo = cd_tributo_pc
	and	a.ie_pago_prev = 'V'
	and	b.ie_origem_titulo != '25' -- Se for de origem pagamento médico (HTML), então não considera, já devia estar o tributo no próprio pagamento
	and	exists (	select	1
			from	pls_pp_lr_base_trib x
			where	x.nr_seq_lote_ret = nr_seq_lote_pc
			and	x.nr_seq_prestador = c.nr_seq_prestador
			and	b.dt_contabil between dt_comp_inicio_pc and dt_comp_fim_pc
			
union all

			select	1
			from	pls_pp_lr_base_trib x
			where	x.nr_seq_lote_ret = nr_seq_lote_pc
			and	x.nr_seq_prestador = c.nr_seq_prestador
			and	b.dt_emissao between dt_comp_inicio_pc and dt_comp_fim_pc)
	group by b.cd_pessoa_fisica, a.nr_sequencia, c.nr_seq_tipo_prestador
	
union all

	select	a.nr_sequencia,
		b.cd_pessoa_fisica,
		c.nr_seq_tipo_prestador,
		max(a.vl_imposto) vl_imposto,
		max(a.vl_base_calculo) vl_base_calculo,
		max(b.vl_titulo) vl_titulo,
		max(b.cd_estabelecimento) cd_estabelecimento,
		obter_se_base_calculo(max(a.nr_titulo), 'TP') ie_base_calculo,
		max(c.nr_seq_prestador) nr_seq_prestador,
		max(a.nr_titulo) nr_titulo,
		max(a.vl_base_adic) vl_base_adic,
		max(a.vl_trib_adic) vl_trib_adic,
		max(a.vl_base_nao_retido) vl_base_nao_retido,
		max(a.vl_nao_retido) vl_nao_retido
	from	pls_pp_prestador_tmp c,
		titulo_pagar b,
		titulo_pagar_imposto a
	where	ie_vencimento_pc in ('R', 'B', 'S') -- por competência, irá utilizar a data do lote
	and	c.ie_tipo_prestador = 'PF'
	and	b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and	b.dt_emissao between dt_comp_inicio_pc and dt_comp_fim_pc
	and	b.ie_situacao in ('A','L')
	and	a.nr_titulo = b.nr_titulo
	and	a.cd_tributo = cd_tributo_pc
	and	a.ie_pago_prev = 'V'
	and	b.ie_origem_titulo != '25' -- Se for de origem pagamento médico (HTML), então não considera, já devia estar o tributo no próprio pagamento
	and	exists (	select	1
			from	pls_pp_lr_base_trib x
			where	x.nr_seq_lote_ret = nr_seq_lote_pc
