	group by b.cd_pessoa_fisica, a.nr_sequencia, c.nr_seq_tipo_prestador;

BEGIN

-- inicializa as variáveis
SELECT * FROM pls_pp_lote_retencao_pck.alimenta_pp_base_acum_tit_pag(	nr_cont_w, tb_nr_sequencia_w, tb_vl_tributo_w, tb_vl_base_calc_w, tb_vl_item_w, tb_cd_pf_w, tb_nr_seq_prest_w, tb_nr_seq_tipo_prest_w, tb_nr_titulo_w, tb_vl_base_adic_w, tb_vl_trib_adic_w, tb_vl_base_nao_ret_w, tb_vl_nao_retido_w, nr_seq_lote_p, cd_tributo_p, nm_usuario_p) INTO STRICT _ora2pg_r;
