			else
				nr_cont_w := nr_cont_w + 1;
			end if;
		end if;
	end if;
end loop;

-- se sobrou algo manda pro banco
SELECT * FROM pls_pp_lote_retencao_pck.alimenta_pp_base_acum_tit_pag(	nr_cont_w, tb_nr_sequencia_w, tb_vl_tributo_w, tb_vl_base_calc_w, tb_vl_item_w, tb_cd_pf_w, tb_nr_seq_prest_w, tb_nr_seq_tipo_prest_w, tb_nr_titulo_w, tb_vl_base_adic_w, tb_vl_trib_adic_w, tb_vl_base_nao_ret_w, tb_vl_nao_retido_w, nr_seq_lote_p, cd_tributo_p, nm_usuario_p) INTO STRICT _ora2pg_r;
 	nr_cont_w := _ora2pg_r.nr_cont_p; tb_nr_sequencia_w := _ora2pg_r.tb_nr_sequencia_p; tb_vl_tributo_w := _ora2pg_r.tb_vl_tributo_p; tb_vl_base_calc_w := _ora2pg_r.tb_vl_base_calc_p; tb_vl_item_w := _ora2pg_r.tb_vl_item_p; tb_cd_pf_w := _ora2pg_r.tb_cd_pf_p; tb_nr_seq_prest_w := _ora2pg_r.tb_nr_seq_prest_p; tb_nr_seq_tipo_prest_w := _ora2pg_r.tb_nr_seq_tipo_prest_p; tb_nr_titulo_w := _ora2pg_r.tb_nr_titulo_p; tb_vl_base_adic_w := _ora2pg_r.tb_vl_base_adic_p; tb_vl_trib_adic_w := _ora2pg_r.tb_vl_trib_adic_p; tb_vl_base_nao_ret_w := _ora2pg_r.tb_vl_base_nao_ret_p; tb_vl_nao_retido_w := _ora2pg_r.tb_vl_nao_retido_p;

END;

