SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.gerar_titulo_trib_retencao ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_titulo_w		titulo_pagar.nr_titulo%type;
cd_estab_financeiro_w	estabelecimento.cd_estab_financeiro%type;

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		a.dt_pgto_tributo,
		abs(a.vl_tributo) vl_tributo,
		a.cd_beneficiario,
		a.nr_seq_trans_reg,
		a.nr_seq_trans_baixa,
		a.nr_seq_classe,
		a.cd_tipo_baixa_neg,
		a.cd_tributo,
		a.cd_pessoa_fisica,
		a.cd_darf
	from	pls_pp_lr_trib_pessoa a
	where	a.nr_seq_lote_ret = nr_seq_lote_pc
	and	a.vl_tributo > 0
	and	a.ie_gerar_titulo_pagar = 'S';

BEGIN

CALL pls_pp_lote_retencao_pck.valida_se_gera_tit_lote(nr_seq_lote_p);

CALL pls_pp_gerar_titulo_pck.carrega_parametros(cd_estabelecimento_p);

select	max(coalesce(cd_estab_financeiro, cd_estabelecimento))
into STRICT	cd_estab_financeiro_w
from	estabelecimento
where	cd_estabelecimento	= cd_estabelecimento_p;

-- abre o cursor com todos os registros que tiveram valor a recolher
for r_c01_w in c01(nr_seq_lote_p) loop

	insert into titulo_pagar(
		nr_titulo, cd_estabelecimento, dt_atualizacao,
		nm_usuario, dt_emissao, dt_contabil,
		dt_vencimento_original, dt_vencimento_atual, vl_titulo,
		vl_saldo_titulo, vl_saldo_juros, vl_saldo_multa,
		cd_moeda, tx_juros, tx_multa,
		cd_tipo_taxa_juro, cd_tipo_taxa_multa, tx_desc_antecipacao,
		ie_situacao, ie_origem_titulo, ie_tipo_titulo,
		cd_cgc, ie_desconto_dia, nr_lote_contabil,
		nr_seq_trans_fin_contab, nr_seq_trans_fin_baixa, ie_status_tributo,
		nr_lote_transf_trib, nr_seq_classe, cd_tipo_baixa_neg,
		ie_status, cd_tributo, ds_observacao_titulo,
		cd_darf, cd_estab_financeiro
	) values (
		nextval('titulo_pagar_seq'), cd_estabelecimento_p, clock_timestamp(),
		nm_usuario_p, trunc(clock_timestamp(),'dd'), trunc(clock_timestamp(),'dd'),
		trunc(r_c01_w.dt_pgto_tributo,'dd'), trunc(r_c01_w.dt_pgto_tributo,'dd'), r_c01_w.vl_tributo,
		r_c01_w.vl_tributo, 0, 0,
		pls_pp_gerar_titulo_pck.cd_moeda_padrao_pagar_w, 0, 0,
		1, 1, 0,
		'A', 4, 4, -- Aberto, Imposto, Imposto
		r_c01_w.cd_beneficiario, 'N', 0,
		r_c01_w.nr_seq_trans_reg, r_c01_w.nr_seq_trans_baixa, 'NT',
		0, r_c01_w.nr_seq_classe, r_c01_w.cd_tipo_baixa_neg,
		'D', r_c01_w.cd_tributo, substr(obter_nome_pf_pj(r_c01_w.cd_pessoa_fisica, null), 1,80),
		r_c01_w.cd_darf, cd_estab_financeiro_w
	) returning nr_titulo into nr_titulo_w;

	CALL atualizar_inclusao_tit_pagar(nr_titulo_w, nm_usuario_p);

	-- jls inserir titulo_pagar_classif não fiz pois falta a conta contábil
	update	pls_pp_lr_trib_pessoa
	set	nr_titulo_pagar = nr_titulo_w
	where	nr_sequencia = r_c01_w.nr_sequencia;

	commit;
end loop;

update	pls_pp_lr_lote
set	dt_geracao_titulo = clock_timestamp()
where	nr_sequencia = nr_seq_lote_p;
commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_pp_lote_retencao_pck.gerar_titulo_trib_retencao ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
