SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.gerar_valores_diferenca ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_vl_trib_dif_w	pls_util_cta_pck.t_number_table;
tb_vl_base_dif_w	pls_util_cta_pck.t_number_table;
tb_ie_natureza_w	pls_util_cta_pck.t_varchar2_table_1;

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	p.nr_sequencia,
		(p.vl_base_pgto - p.vl_base_calculo),
		(p.vl_tributo_pago - p.vl_tributo),
		case	when(p.vl_tributo_pago - p.vl_tributo) < 0 then 'D'
			when(p.vl_tributo_pago - p.vl_tributo) > 0 then 'P'
			else 'S'
		end ie_natureza
	from	pls_pp_lr_trib_pessoa p,
		tributo t
	where	t.cd_tributo		= p.cd_tributo
	and	p.nr_seq_lote_ret 	= nr_seq_lote_pc
	and	t.ie_tipo_tributo 	= 'INSS';


BEGIN
-- Recalcular arredondamento de 1 centavo
CALL pls_pp_lote_retencao_pck.recalc_arred_lote_ret( nr_seq_lote_p );

open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into	tb_nr_sequencia_w, tb_vl_base_dif_w, tb_vl_trib_dif_w,
					tb_ie_natureza_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		update	pls_pp_lr_trib_pessoa
		set	vl_diferenca_base = tb_vl_base_dif_w(i),
			vl_diferenca_trib = tb_vl_trib_dif_w(i),
			ie_natureza = tb_ie_natureza_w(i)
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
