CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.gerar_valores_retencao_pgto ( nr_seq_lote_pgto_p pls_pp_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_nr_seq_lr_trib_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_prest_w	pls_util_cta_pck.t_number_table;
tb_vl_trib_item_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_evento_w	pls_util_cta_pck.t_number_table;

c01 CURSOR(	nr_seq_lote_pgto_pc	pls_pp_lr_lote.nr_sequencia%type,
		nr_seq_evt_prov_pc	pls_evento.nr_sequencia%type,
		nr_seq_evt_desc_pc	pls_evento.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		max(a.vl_diferenca_trib),
		nr_seq_evt_prov_pc,
		pls_pp_lote_retencao_pck.obter_prest_pgto_retencao(	nr_seq_lote_pgto_pc, a.nr_sequencia,
									max(a.cd_pessoa_fisica), nr_seq_evt_prov_pc) nr_seq_prestador
	from	pls_pp_lr_trib_pessoa a,
		pls_pp_prestador_tmp b,
		pls_pp_lr_lote c
	where	b.ie_tipo_prestador = 'PF'
	and	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	a.ie_natureza = 'P'
	and	a.vl_diferenca_trib > 0
	and	c.nr_sequencia = a.nr_seq_lote_ret
	and	(c.dt_geracao_titulo IS NOT NULL AND c.dt_geracao_titulo::text <> '')
	and not exists (	SELECT	1
			from	pls_pp_item_lote x
			where	x.nr_seq_lr_trib_pess = a.nr_sequencia
			and	x.ie_cancelado = 'N')
	and 	exists (	select	1
			from	pls_pp_lote_prest_event z
			where	z.nr_seq_lote = nr_seq_lote_pgto_pc
			and	z.nr_seq_prestador = b.nr_seq_prestador
			and	z.nr_seq_evento = nr_seq_evt_prov_pc)
	group by a.nr_sequencia
	
union all

	select	a.nr_sequencia,
		max(a.vl_diferenca_trib),
		nr_seq_evt_desc_pc,
		pls_pp_lote_retencao_pck.obter_prest_pgto_retencao(	nr_seq_lote_pgto_pc, a.nr_sequencia,
									max(a.cd_pessoa_fisica), nr_seq_evt_desc_pc) nr_seq_prestador
	from	pls_pp_lr_trib_pessoa a,
		pls_pp_prestador_tmp b,
		pls_pp_lr_lote c
	where	b.ie_tipo_prestador = 'PF'
	and	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	a.ie_natureza = 'D'
	and	a.vl_diferenca_trib < 0
	and	c.nr_sequencia = a.nr_seq_lote_ret
	and	(c.dt_geracao_titulo IS NOT NULL AND c.dt_geracao_titulo::text <> '')
	and not exists (	select	1
			from	pls_pp_item_lote x
			where	x.nr_seq_lr_trib_pess = a.nr_sequencia) 
	and 	exists (	select	1
			from	pls_pp_lote_prest_event z
			where	z.nr_seq_lote = nr_seq_lote_pgto_pc
			and	z.nr_seq_prestador = b.nr_seq_prestador
			and	z.nr_seq_evento = nr_seq_evt_desc_pc)
	group by a.nr_sequencia;


BEGIN

open c01(	nr_seq_lote_pgto_p,
		pls_pp_lote_pagamento_pck.nr_seq_evento_trib_prov_w, 
		pls_pp_lote_pagamento_pck.nr_seq_evento_trib_lm_w);
loop
	fetch c01 bulk collect into	tb_nr_seq_lr_trib_w, tb_vl_trib_item_w, tb_nr_seq_evento_w,
					tb_nr_seq_prest_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_lr_trib_w.count = 0;

	forall i in tb_nr_seq_lr_trib_w.first..tb_nr_seq_lr_trib_w.last
		insert into pls_pp_item_lote(
			nr_sequencia, nm_usuario, nm_usuario_nrec,
			dt_atualizacao, dt_atualizacao_nrec, ie_tipo_item,
			nr_seq_evento, nr_seq_lote, nr_seq_prestador,
			vl_glosa, vl_item, vl_liquido,
			nr_seq_lr_trib_pess, vl_desconto_tributo, vl_acao_negativo,
			ie_cancelado, nr_seq_prestador_origem
		) values (
			nextval('pls_pp_item_lote_seq'), nm_usuario_p, nm_usuario_p,
			clock_timestamp(), clock_timestamp(), '7',
			tb_nr_seq_evento_w(i), nr_seq_lote_pgto_p, tb_nr_seq_prest_w(i),
			0, tb_vl_trib_item_w(i), tb_vl_trib_item_w(i), 
			tb_nr_seq_lr_trib_w(i), 0, 0,
			'N', tb_nr_seq_prest_w(i)
		);
	commit;
end loop;
close c01;
		
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
