CREATE OR REPLACE FUNCTION pls_pp_lote_retencao_pck.obter_lote_pgto_diferenca ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(150);

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	a.nr_seq_lote
	from	pls_pp_lr_trib_pessoa b,
		pls_pp_item_lote a
	where	b.nr_seq_lote_ret = nr_seq_lote_pc
	and	a.nr_seq_lr_trib_pess = b.nr_sequencia
	and	a.ie_tipo_item = '7';

BEGIN

for r_c01_w in c01(nr_seq_lote_p) loop

	ds_retorno_w := pls_util_pck.concatena_string(	ds_retorno_w, r_c01_w.nr_seq_lote, ', ', 150);
end loop;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
