SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.recalc_arred_lote_ret ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type) AS $body$
DECLARE


c01 CURSOR(	nr_seq_lote_pc	pls_pp_lr_lote.nr_sequencia%type) FOR
	SELECT	p.nr_sequencia,
		(p.vl_tributo_pago - p.vl_tributo) vl_diferenca,
		p.vl_tributo_pago
	from	pls_pp_lr_trib_pessoa p,
		tributo t
	where	t.cd_tributo		= p.cd_tributo
	and	p.nr_seq_lote_ret	= nr_seq_lote_pc
	and	t.ie_tipo_tributo 	= 'INSS';

BEGIN
for r_c01_w in c01( nr_seq_lote_p ) loop
	-- Se existe diferença de 1 centavo, não deve ter a diferença
	if (r_c01_w.vl_diferenca between -0.01 and 0.01) and (r_c01_w.vl_diferenca != 0) then
		update	pls_pp_lr_trib_pessoa
		set	vl_tributo	= r_c01_w.vl_tributo_pago
		where	nr_sequencia	= r_c01_w.nr_sequencia;
	end if;
end loop;
commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
