CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.valida_se_canc_tit_lote_fecha ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type) AS $body$
DECLARE


ds_mensagem_w	varchar(255);


BEGIN
-- retornar todos os lotes de pagamento em que os registros de ajuste tenham entrado.
ds_mensagem_w := pls_pp_lote_retencao_pck.obter_lote_pgto_diferenca(nr_seq_lote_p);

-- se retornou algo então não permite cancelar os títulos
if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then

	-- Não é possível cancelar os títulos do lote de fechamento. 
	-- Os registros deste lote já estão no(s) lote(s) de pagamento:
	-- #@NR_SEQ_LOTE_P#@
	CALL wheb_mensagem_pck.exibir_mensagem_abort(471865, 'NR_SEQ_LOTE_P=' || ds_mensagem_w);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
