CREATE OR REPLACE PROCEDURE pls_pp_lote_retencao_pck.valida_se_gera_tit_lote ( nr_seq_lote_p pls_pp_lr_lote.nr_sequencia%type) AS $body$
DECLARE


qt_registros_w	integer;


BEGIN
-- verifica se existem itens do lote que não víncularam a regras de cálculo
select	count(1)
into STRICT	qt_registros_w
from	pls_pp_lr_trib_pessoa a
where	a.nr_seq_lote_ret = nr_seq_lote_p
and	a.vl_diferenca_trib <> 0
and not exists (	SELECT	1
		from	tributo_conta_pagar x
		where	x.nr_sequencia = a.nr_seq_trib_conta_pagar)  LIMIT 1;

if (qt_registros_w > 0) then

	-- Não é possível gerar os títulos, existem registros que não encontraram regra para cálculo de tributo no lote de retenção.
	-- Por favor verifique.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(471870);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
